job.command:
starver SL15c

setenv PRODPATH &basePath;/production/&prodId;
setenv LOGPATH  &basePath;/log/&prodId;

mkdir -p ${PRODPATH} ${LOGPATH}

if ("&mMode;" == "1") then
    &basePath;/parseFileList.csh ${FILELIST} &basePath; &prodId; &mMode; &treeName; ${JOBID} &rootMacro;
else
    root4star -q -b -l StRoot/macros/&rootMacro;\(\"$FILELIST\",\"&prodId;_$JOBINDEX\",&mMode;\) >& ${JOBID}.log
    tar -zcvf ${JOBID}.log.tgz ${JOBID}.log
endif
